import React, { useEffect, useState } from 'react';
import fetchAdjustedPrices from './services/api';
import SalesTrendChart from './SalesTrendChart';
import Dashboard from './components/Dashboard'; // Adjust the path if needed

const calculateInsights = (products) => {
    const totalRevenue = products.reduce((sum, product) => sum + product.adjusted_price * product.sales_last_30_days, 0);
    const lowInventory = products.filter(product => product.inventory < 10).length;

    return { totalRevenue, lowInventory };
};

const App = () => {
    const [adjustedPrices, setAdjustedPrices] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await fetchAdjustedPrices();
                setAdjustedPrices(data || []);
            } catch (err) {
                setError('Failed to fetch adjusted prices.');
            }
        };

        fetchData();
    }, []);

    const insights = calculateInsights(adjustedPrices);

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
            <h1>Dynamic Pricing Dashboard</h1>
            {error ? (
                <p style={{ color: 'red' }}>{error}</p>
            ) : (
                <div>
                    {/* Insights Section */}
                    <div style={{ marginBottom: '20px' }}>
                        <h2>Key Insights</h2>
                        <p><strong>Total Revenue:</strong> ${insights.totalRevenue.toFixed(2)}</p>
                        <p><strong>Products with Low Inventory:</strong> {insights.lowInventory}</p>
                    </div>

                    {/* Table Section */}
                    <table border="1" cellPadding="10" style={{ width: '100%', textAlign: 'left' }}>
                        <thead>
                            <tr>
                                <th>Product ID</th>
                                <th>Base Price</th>
                                <th>Inventory</th>
                                <th>Sales (Last 30 Days)</th>
                                <th>Average Rating</th>
                                <th>Competitor Price</th>
                                <th>Adjusted Price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {adjustedPrices.map((product, index) => (
                                <tr key={index}>
                                    <td>{product.product_id}</td>
                                    <td>${product.base_price.toFixed(2)}</td>
                                    <td>{product.inventory}</td>
                                    <td>{product.sales_last_30_days}</td>
                                    <td>{product.average_rating}</td>
                                    <td>${product.competitor_price.toFixed(2)}</td>
                                    <td>${product.adjusted_price.toFixed(2)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* Chart Section */}
                    <SalesTrendChart />
                </div>
            )}
        </div>
    );
};

export default App;
