import React, { useEffect, useState } from 'react';
import fetchAdjustedPrices from './services/api'; // Your API service

const Dashboard = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetchAdjustedPrices().then((data) => {
            setProducts(data); // Save the adjusted products in state
        }).catch((error) => {
            console.error("Error fetching adjusted prices:", error);
        });
    }, []);

    return (
        <div>
            <h1>Dynamic Pricing Dashboard</h1>
            <table>
                <thead>
                    <tr>
                        <th>Product ID</th>
                        <th>Base Price</th>
                        <th>Inventory</th>
                        <th>Competitor Price</th>
                        <th>Adjusted Price</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.product_id}>
                            <td>{product.product_id}</td>
                            <td>${product.base_price.toFixed(2)}</td>
                            <td>{product.inventory}</td>
                            <td>${product.competitor_price.toFixed(2)}</td>
                            <td>${product.adjusted_price.toFixed(2)}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
