import axios from 'axios';

const fetchAdjustedPrices = async () => {
    const payload = [
        {
            product_id: "P001",
            base_price: 100.0,
            inventory: 15,
            sales_last_30_days: 120,
            average_rating: 4.5,
            competitor_price: 90.0,
            avg_units_sold: 30,
        },
        {
            product_id: "P002",
            base_price: 200.0,
            inventory: 50,
            sales_last_30_days: 40,
            average_rating: 4.0,
            competitor_price: 195.0,
            avg_units_sold: 25,
        },
    ];

    try {
        const response = await axios.post('http://127.0.0.1:5000/adjust-prices', payload, {
            headers: {
                'Content-Type': 'application/json',
            },
        });
        console.log('Adjusted Prices:', response.data);
        return response.data; // Ensure the function returns the data
    } catch (error) {
        console.error('Error fetching adjusted prices:', error);
        return []; // Return an empty array in case of an error
    }
};

export default fetchAdjustedPrices;
