import React, { useEffect, useState } from "react";
import fetchAdjustedPrices from "./services/api";
import Charts from "./components/Charts";

const App = () => {
  const [adjustedPrices, setAdjustedPrices] = useState([]);
  const [salesData, setSalesData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchAdjustedPrices();
        setAdjustedPrices(data || []);

        // Mock Sales Data for Sales Trends Chart
        setSalesData([
          { date: "2024-10-01", product_id: "P001", units_sold: 5 },
          { date: "2024-10-02", product_id: "P001", units_sold: 10 },
          { date: "2024-10-01", product_id: "P002", units_sold: 3 },
          { date: "2024-10-01", product_id: "P003", units_sold: 1 },
        ]);
      } catch (err) {
        setError("Failed to fetch adjusted prices.");
      }
    };

    fetchData();
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Dynamic Pricing Dashboard</h1>
      {error ? (
        <p style={{ color: "red" }}>{error}</p>
      ) : adjustedPrices.length === 0 ? (
        <p>No data available.</p>
      ) : (
        <div>
          {/* Display Charts */}
          <Charts
            salesData={salesData}
            competitorData={adjustedPrices.map((product) => ({
              product_id: product.product_id,
              base_price: product.base_price,
              adjusted_price: product.adjusted_price,
              competitor_price: product.competitor_price,
            }))}
          />
        </div>
      )}
    </div>
  );
};

export default App;
