import axios from 'axios';
import API_BASE_URL from './config';

const fetchAdjustedPrices = async (payload = [
    {
        product_id: "P001",
        base_price: 100.0,
        inventory: 15,
        sales_last_30_days: 120,
        average_rating: 4.5,
        competitor_price: 90.0,
        avg_units_sold: 30,
    },
    {
        product_id: "P002",
        base_price: 200.0,
        inventory: 50,
        sales_last_30_days: 40,
        average_rating: 4.0,
        competitor_price: 195.0,
        avg_units_sold: 25,
    },
]) => {
    try {
        console.log("Payload being sent to /adjust-prices:", payload);

        const response = await axios.post(`${API_BASE_URL}/adjust-prices`, payload, {
            headers: {
                'Content-Type': 'application/json',
            },
        });

        console.log('Adjusted Prices:', response.data);
        return response.data;
    } catch (error) {
        console.error('Error fetching adjusted prices:', error);
        throw new Error('Failed to fetch adjusted prices. Please try again later.');
    }
};
