import React, { useEffect, useState } from "react";
import { fetchAdjustedPrices } from "./services/api"; // Ensure this path is correct

function App() {
  const [products, setProducts] = useState([]); // State for storing fetched data
  const [error, setError] = useState(null); // State for error handling
  const [loading, setLoading] = useState(true); // State for loading indicator

  // Add the useEffect here
  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchAdjustedPrices(); // Fetch data from API
        console.log("Fetched data:", data); // Debugging: Log fetched data
        setProducts(data); // Update state with the fetched data
      } catch (err) {
        console.error("Error fetching data:", err); // Debugging: Log errors
        setError("Failed to fetch adjusted prices.");
      } finally {
        setLoading(false); // Stop the loading indicator
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures this runs only once after the component mounts

  // Loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Error state
  if (error) {
    return <div>{error}</div>;
  }

  // Render your table
  return (
    <div>
      <h1>Dynamic Pricing System</h1>
      <table>
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Base Price</th>
            <th>Adjusted Price</th>
            <th>Inventory</th>
            <th>Sales Last 30 Days</th>
            <th>Average Rating</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_id}</td>
              <td>{product.base_price}</td>
              <td>{product.adjusted_price}</td>
              <td>{product.inventory}</td>
              <td>{product.sales_last_30_days}</td>
              <td>{product.average_rating}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
