import React, { useEffect, useState } from 'react';
import fetchAdjustedPrices from './services/api';

const App = () => {
    const [adjustedPrices, setAdjustedPrices] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await fetchAdjustedPrices(); // Fetch data from the API
                setAdjustedPrices(data || []); // Ensure data is not null or undefined
            } catch (err) {
                setError('Failed to fetch adjusted prices.');
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <h1>Adjusted Prices</h1>
            {error ? (
                <p style={{ color: 'red' }}>{error}</p>
            ) : adjustedPrices.length > 0 ? (
                <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%', textAlign: 'left' }}>
                    <thead>
                        <tr>
                            <th>Product ID</th>
                            <th>Base Price</th>
                            <th>Inventory</th>
                            <th>Sales (Last 30 Days)</th>
                            <th>Average Rating</th>
                            <th>Competitor Price</th>
                            <th>Adjusted Price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {adjustedPrices.map((product, index) => (
                            <tr key={index}>
                                <td>{product.product_id}</td>
                                <td>${product.base_price.toFixed(2)}</td>
                                <td>{product.inventory}</td>
                                <td>{product.sales_last_30_days}</td>
                                <td>{product.average_rating}</td>
                                <td>${product.competitor_price.toFixed(2)}</td>
                                <td>${product.adjusted_price.toFixed(2)}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default App;
